import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import axios from 'axios'; // axios ÏûÑÌè¨Ìä∏ Ï†úÍ±∞
import '../css/Buyers.css';
import logoImage from '../assets/logo.png';

function ResultScreen() {
    const navigate = useNavigate();

    const [verificationIds, setVerificationIds] = useState([]);
    const [currentVerificationId, setCurrentVerificationId] = useState(null); // ÏÇ¨ÏßÑ Î≥¥Ïó¨Ï§Ñ verification ID
    const [photoUrl, setPhotoUrl] = useState(null);
    const [loadingPhoto, setLoadingPhoto] = useState(false);
    const [photoError, setPhotoError] = useState(null);
    const [selectedFeedback, setSelectedFeedback] = useState(null);

    // Ïã§Ï†ú Î∞±ÏóîÎìú Ïó∞Îèô ÎåÄÏã† ÏûÑÏùòÏùò ÏÑ∏ÏÖò ID ÏÇ¨Ïö©
    const verificationLinkId = "dummySessionId123"; 

    useEffect(() => {
        // if (!verificationLinkId) return; // ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞Ïù¥ÎØÄÎ°ú Ïù¥ Ï°∞Í±¥Î¨∏ÏùÄ ÌïÑÏöî ÏóÜÏùå

        // 2. verificationLinkIdÎ°ú verification ID Î™©Î°ù Î∞õÏïÑÏò§Í∏∞ (ÏòàÏ†ú Îç∞Ïù¥ÌÑ∞)
        const fetchVerificationIds = async () => {
            console.log("Ïù∏Ï¶ù ID Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ ÏãúÎÆ¨Î†àÏù¥ÏÖò...");
            // Ïã§Ï†ú API Ìò∏Ï∂ú ÎåÄÏã† setTimeoutÏúºÎ°ú ÎπÑÎèôÍ∏∞ ÎèôÏûë ÏãúÎÆ¨Î†àÏù¥ÏÖò
            setTimeout(() => {
                const dummyIds = ['VERIF-001', 'VERIF-002', 'VERIF-003', 'VERIF-004']; // ÏòàÏ†ú ID Î™©Î°ù
                setVerificationIds(dummyIds);
                if (dummyIds.length > 0) {
                    setCurrentVerificationId(dummyIds[0]); // Ïòà: Ï≤´ Î≤àÏß∏ IDÎ°ú ÏÇ¨ÏßÑ ÌëúÏãú
                }
                console.log("Ïù∏Ï¶ù ID Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ ÏôÑÎ£å:", dummyIds);
            }, 500); // 0.5Ï¥à ÏßÄÏó∞
        };

        fetchVerificationIds();
    }, [/* verificationLinkId */]); // ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞Ïù¥ÎØÄÎ°ú ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ÏóêÏÑú Ï†úÍ±∞ÌïòÍ±∞ÎÇò Îπà Î∞∞Ïó¥Î°ú Îë†

    useEffect(() => {
        if (!currentVerificationId) {
            setPhotoUrl(null);
            return;
        }

        // 3. ÌòÑÏû¨ verificationIdÎ°ú ÏÇ¨ÏßÑ URL Î∞õÏïÑÏò§Í∏∞ (ÏòàÏ†ú Îç∞Ïù¥ÌÑ∞)
        const fetchPhotoUrl = async () => {
            setLoadingPhoto(true);
            setPhotoError(null);
            console.log(`'${currentVerificationId}'Ïùò ÏÇ¨ÏßÑ Î∂àÎü¨Ïò§Í∏∞ ÏãúÎÆ¨Î†àÏù¥ÏÖò...`);

            // Ïã§Ï†ú API Ìò∏Ï∂ú ÎåÄÏã† setTimeoutÏúºÎ°ú ÎπÑÎèôÍ∏∞ ÎèôÏûë ÏãúÎÆ¨Î†àÏù¥ÏÖò
            setTimeout(() => {
                let url = null;
                // currentVerificationIdÏóê Îî∞Îùº Îã§Î•∏ ÏòàÏ†ú Ïù¥ÎØ∏ÏßÄ URL Î∞òÌôò
                switch (currentVerificationId) {
                    case 'VERIF-001':
                        url = 'https://via.placeholder.com/400x300/FF0000/FFFFFF?text=PHOTO+001'; // Îπ®Í∞ÑÏÉâ ÌîåÎ†àÏù¥Ïä§ÌôÄÎçî
                        break;
                    case 'VERIF-002':
                        url = 'https://via.placeholder.com/400x300/00FF00/000000?text=PHOTO+002'; // Ï¥àÎ°ùÏÉâ ÌîåÎ†àÏù¥Ïä§ÌôÄÎçî
                        break;
                    case 'VERIF-003':
                        url = 'https://via.placeholder.com/400x300/0000FF/FFFFFF?text=PHOTO+003'; // ÌååÎûÄÏÉâ ÌîåÎ†àÏù¥Ïä§ÌôÄÎçî
                        break;
                    case 'VERIF-004':
                        // ÌäπÏ†ï IDÏóê ÎåÄÌïú Ïò§Î•ò ÏãúÎÆ¨Î†àÏù¥ÏÖò
                        setPhotoError('VERIF-004: ÏÇ¨ÏßÑÏùÑ Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
                        url = null;
                        break;
                    default:
                        url = 'https://via.placeholder.com/400x300/CCCCCC/808080?text=No+Photo'; // Í∏∞Î≥∏ ÌîåÎ†àÏù¥Ïä§ÌôÄÎçî
                }
                setPhotoUrl(url);
                setLoadingPhoto(false);
                console.log(`'${currentVerificationId}'Ïùò ÏÇ¨ÏßÑ Î∂àÎü¨Ïò§Í∏∞ ÏôÑÎ£å:`, url || 'Ïò§Î•ò');
            }, 800); // 0.8Ï¥à ÏßÄÏó∞
        };

        fetchPhotoUrl();
    }, [currentVerificationId]); // currentVerificationIdÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ ÏÇ¨ÏßÑÏùÑ Î∂àÎü¨Ïò¥

    const goToHome = () => {
        console.log("ÌôàÏúºÎ°ú Ïù¥Îèô");
        navigate('/');
    };

    const handleDone = () => {
        console.log('ÏÑ†ÌÉùÎêú ÌîºÎìúÎ∞±:', selectedFeedback);
        console.log('ÏôÑÎ£å Î≤ÑÌäº ÌÅ¥Î¶≠. /endÎ°ú Ïù¥Îèô.');
        navigate('/end');
    };

    return (
        <div className="container">
            <div className="header-small">
                <div className="logo-with-text" onClick={goToHome}>
                    <img src={logoImage} alt="SABER Logo" className="logo-image" />
                    <div className="logo-text">SABER</div>
                </div>
                <div className="menu-icon-small">‚ò∞</div>
            </div>

            <div className="progress-bar-container">
                <div className="progress-bar-100" />
            </div>

            <h2 className="title-small">Ïù∏Ï¶ù Í≤∞Í≥º ÌôïÏù∏</h2>

            {/* Ïù∏Ï¶ù ID Î™©Î°ù Î≤ÑÌäº */}
            <div className="verification-id-list" style={{ marginBottom: '1rem' }}>
                {verificationIds.length === 0 && <p>Ïù∏Ï¶ù IDÍ∞Ä ÏóÜÏäµÎãàÎã§. (Î°úÎî© Ï§ëÏù¥Í±∞ÎÇò ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå)</p>}
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem', justifyContent: 'center' }}>
                    {verificationIds.map(id => (
                        <button
                            key={id}
                            onClick={() => setCurrentVerificationId(id)}
                            className={`verification-id-button ${currentVerificationId === id ? 'selected' : ''}`}
                            style={{
                                padding: '0.5rem 1rem',
                                borderRadius: '4px',
                                border: currentVerificationId === id ? '2px solid #007bff' : '1px solid #ccc',
                                backgroundColor: currentVerificationId === id ? '#e6f7ff' : '#fff', // ÏÑ†ÌÉù Ïãú Î∞∞Í≤ΩÏÉâ Î≥ÄÍ≤Ω
                                color: currentVerificationId === id ? '#007bff' : '#333', // ÏÑ†ÌÉù Ïãú Í∏ÄÏûêÏÉâ Î≥ÄÍ≤Ω
                                cursor: 'pointer',
                                userSelect: 'none',
                                transition: 'all 0.2s ease-in-out' // Î∂ÄÎìúÎü¨Ïö¥ Ï†ÑÌôò Ìö®Í≥º
                            }}
                        >
                            {id}
                        </button>
                    ))}
                </div>
            </div>

            <div className="media-box">
                {loadingPhoto && <p>ÏÇ¨ÏßÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>}
                {photoError && <p style={{ color: 'red', textAlign: 'center' }}>{photoError}</p>}
                {photoUrl && <img src={photoUrl} alt="Verification" style={{ maxWidth: '100%', maxHeight: 400, display: 'block', margin: '0 auto' }} />}
                {!loadingPhoto && !photoUrl && !photoError && <p style={{ textAlign: 'center' }}>ÏÇ¨ÏßÑÏù¥ ÏóÜÏäµÎãàÎã§.</p>}
            </div>

            <div className="slide-indicator" />

            <div className="feedback-container">
                <div className="feedback-label">Ìï≠Î™© Ïù∏Ï¶ù ÎßåÏ°±ÎèÑ</div>
                <div className="emoji-buttons">
                    <button
                        className={`emoji-button ${selectedFeedback === 'satisfied' ? 'selected' : ''}`}
                        onClick={() => setSelectedFeedback('satisfied')}
                    >
                        üòä ÎßåÏ°±
                    </button>
                    <button
                        className={`emoji-button ${selectedFeedback === 'unsatisfied' ? 'selected' : ''}`}
                        onClick={() => setSelectedFeedback('unsatisfied')}
                    >
                        üòï Î∂àÎßåÏ°±
                    </button>
                </div>
            </div>

            <button className="button" onClick={handleDone}>ÏôÑÎ£å</button>
        </div>
    );
}

export default ResultScreen;
