import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import axios from 'axios'; // axios 임포트 제거
import '../css/Buyers.css';
import logoImage from '../assets/logo.png';

function ResultScreen() {
    const navigate = useNavigate();

    const [verificationIds, setVerificationIds] = useState([]);
    const [currentVerificationId, setCurrentVerificationId] = useState(null); // 사진 보여줄 verification ID
    const [photoUrl, setPhotoUrl] = useState(null);
    const [loadingPhoto, setLoadingPhoto] = useState(false);
    const [photoError, setPhotoError] = useState(null);
    const [selectedFeedback, setSelectedFeedback] = useState(null);

    // 실제 백엔드 연동 대신 임의의 세션 ID 사용
    const verificationLinkId = "dummySessionId123"; 

    useEffect(() => {
        // if (!verificationLinkId) return; // 더미 데이터이므로 이 조건문은 필요 없음

        // 2. verificationLinkId로 verification ID 목록 받아오기 (예제 데이터)
        const fetchVerificationIds = async () => {
            console.log("인증 ID 목록 불러오기 시뮬레이션...");
            // 실제 API 호출 대신 setTimeout으로 비동기 동작 시뮬레이션
            setTimeout(() => {
                const dummyIds = ['VERIF-001', 'VERIF-002', 'VERIF-003', 'VERIF-004']; // 예제 ID 목록
                setVerificationIds(dummyIds);
                if (dummyIds.length > 0) {
                    setCurrentVerificationId(dummyIds[0]); // 예: 첫 번째 ID로 사진 표시
                }
                console.log("인증 ID 목록 불러오기 완료:", dummyIds);
            }, 500); // 0.5초 지연
        };

        fetchVerificationIds();
    }, [/* verificationLinkId */]); // 더미 데이터이므로 의존성 배열에서 제거하거나 빈 배열로 둠

    useEffect(() => {
        if (!currentVerificationId) {
            setPhotoUrl(null);
            return;
        }

        // 3. 현재 verificationId로 사진 URL 받아오기 (예제 데이터)
        const fetchPhotoUrl = async () => {
            setLoadingPhoto(true);
            setPhotoError(null);
            console.log(`'${currentVerificationId}'의 사진 불러오기 시뮬레이션...`);

            // 실제 API 호출 대신 setTimeout으로 비동기 동작 시뮬레이션
            setTimeout(() => {
                let url = null;
                // currentVerificationId에 따라 다른 예제 이미지 URL 반환
                switch (currentVerificationId) {
                    case 'VERIF-001':
                        url = 'https://via.placeholder.com/400x300/FF0000/FFFFFF?text=PHOTO+001'; // 빨간색 플레이스홀더
                        break;
                    case 'VERIF-002':
                        url = 'https://via.placeholder.com/400x300/00FF00/000000?text=PHOTO+002'; // 초록색 플레이스홀더
                        break;
                    case 'VERIF-003':
                        url = 'https://via.placeholder.com/400x300/0000FF/FFFFFF?text=PHOTO+003'; // 파란색 플레이스홀더
                        break;
                    case 'VERIF-004':
                        // 특정 ID에 대한 오류 시뮬레이션
                        setPhotoError('VERIF-004: 사진을 불러올 수 없습니다.');
                        url = null;
                        break;
                    default:
                        url = 'https://via.placeholder.com/400x300/CCCCCC/808080?text=No+Photo'; // 기본 플레이스홀더
                }
                setPhotoUrl(url);
                setLoadingPhoto(false);
                console.log(`'${currentVerificationId}'의 사진 불러오기 완료:`, url || '오류');
            }, 800); // 0.8초 지연
        };

        fetchPhotoUrl();
    }, [currentVerificationId]); // currentVerificationId가 변경될 때마다 사진을 불러옴

    const goToHome = () => {
        console.log("홈으로 이동");
        navigate('/');
    };

    const handleDone = () => {
        console.log('선택된 피드백:', selectedFeedback);
        console.log('완료 버튼 클릭. /end로 이동.');
        navigate('/end');
    };

    return (
        <div className="container">
            <div className="header-small">
                <div className="logo-with-text" onClick={goToHome}>
                    <img src={logoImage} alt="SABER Logo" className="logo-image" />
                    <div className="logo-text">SABER</div>
                </div>
                <div className="menu-icon-small">☰</div>
            </div>

            <div className="progress-bar-container">
                <div className="progress-bar-100" />
            </div>

            <h2 className="title-small">인증 결과 확인</h2>

            {/* 인증 ID 목록 버튼 */}
            <div className="verification-id-list" style={{ marginBottom: '1rem' }}>
                {verificationIds.length === 0 && <p>인증 ID가 없습니다. (로딩 중이거나 더미 데이터 없음)</p>}
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem', justifyContent: 'center' }}>
                    {verificationIds.map(id => (
                        <button
                            key={id}
                            onClick={() => setCurrentVerificationId(id)}
                            className={`verification-id-button ${currentVerificationId === id ? 'selected' : ''}`}
                            style={{
                                padding: '0.5rem 1rem',
                                borderRadius: '4px',
                                border: currentVerificationId === id ? '2px solid #007bff' : '1px solid #ccc',
                                backgroundColor: currentVerificationId === id ? '#e6f7ff' : '#fff', // 선택 시 배경색 변경
                                color: currentVerificationId === id ? '#007bff' : '#333', // 선택 시 글자색 변경
                                cursor: 'pointer',
                                userSelect: 'none',
                                transition: 'all 0.2s ease-in-out' // 부드러운 전환 효과
                            }}
                        >
                            {id}
                        </button>
                    ))}
                </div>
            </div>

            <div className="media-box">
                {loadingPhoto && <p>사진 불러오는 중...</p>}
                {photoError && <p style={{ color: 'red', textAlign: 'center' }}>{photoError}</p>}
                {photoUrl && <img src={photoUrl} alt="Verification" style={{ maxWidth: '100%', maxHeight: 400, display: 'block', margin: '0 auto' }} />}
                {!loadingPhoto && !photoUrl && !photoError && <p style={{ textAlign: 'center' }}>사진이 없습니다.</p>}
            </div>

            <div className="slide-indicator" />

            <div className="feedback-container">
                <div className="feedback-label">항목 인증 만족도</div>
                <div className="emoji-buttons">
                    <button
                        className={`emoji-button ${selectedFeedback === 'satisfied' ? 'selected' : ''}`}
                        onClick={() => setSelectedFeedback('satisfied')}
                    >
                        😊 만족
                    </button>
                    <button
                        className={`emoji-button ${selectedFeedback === 'unsatisfied' ? 'selected' : ''}`}
                        onClick={() => setSelectedFeedback('unsatisfied')}
                    >
                        😕 불만족
                    </button>
                </div>
            </div>

            <button className="button" onClick={handleDone}>완료</button>
        </div>
    );
}

export default ResultScreen;
