import React, { useState, useRef, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
// 필요한 아이콘 임포트
import { Camera, Upload, MessageSquare, AlertCircle, CheckCircle, XCircle } from 'lucide-react'; 

function VerificationCamera() {
    const { id: verificationId } = useParams();
    const navigate = useNavigate();

    const [capturedPhotoDataUrl, setCapturedPhotoDataUrl] = useState(""); // 캡쳐된 사진의 Data URL (미리보기용)
    const [capturedPhotoBlob, setCapturedPhotoBlob] = useState(null); // 캡쳐된 사진의 Blob (업로드용)
    const [comment, setComment] = useState("");
    const videoRef = useRef(null);
    const imageCaptureRef = useRef(null); // ImageCapture 객체를 저장할 useRef
    const [uploading, setUploading] = useState(false);
    const [cameraActive, setCameraActive] = useState(false); // 카메라 활성화 상태
    const [message, setMessage] = useState({ text: '', type: '' }); // 메시지 모달용 상태
    const [showSubmitModal, setShowSubmitModal] = useState(false); // 제출 모달 표시 여부

    // 메시지 모달 표시 함수
    const showMessage = (text, type = 'info') => {
        setMessage({ text, type });
        setTimeout(() => setMessage({ text: '', type: '' }), 3000); // 3초 후 메시지 사라짐
    };

    useEffect(() => {
        // 컴포넌트 마운트 시 카메라 자동 시작
        startCamera(); 

        return () => {
            // 컴포넌트 언마운트 시 카메라 스트림 정리
            if (videoRef.current && videoRef.current.srcObject) {
                videoRef.current.srcObject.getTracks().forEach((track) => track.stop());
                setCameraActive(false);
            }
        };
    }, []);

    const startCamera = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ 
                video: {
                    facingMode: 'environment' // 후면 카메라 우선 사용
                }
            });
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                setCameraActive(true); // 카메라 활성화 상태 업데이트

                // ImageCapture 객체 초기화
                const videoTrack = stream.getVideoTracks()[0];
                imageCaptureRef.current = new ImageCapture(videoTrack);

                showMessage('카메라가 시작되었습니다.', 'success');
            }
        } catch (err) {
            console.error("카메라 시작 오류:", err);
            showMessage("카메라 권한이 필요합니다. 브라우저 설정을 확인해주세요.", 'error');
            setCameraActive(false); // 카메라 비활성화 상태 유지
        }
    };

    const takePhoto = async () => {
        if (!imageCaptureRef.current || !cameraActive) {
            showMessage("카메라를 먼저 시작해주세요.", 'info');
            return;
        }

        try {
            // ImageCapture.takePhoto()를 사용하여 직접 Blob 형태로 사진 캡처
            const photoBlob = await imageCaptureRef.current.takePhoto();
            setCapturedPhotoBlob(photoBlob); // 업로드용 Blob 저장

            // 미리보기용 Data URL 생성 (Blob을 Data URL로 변환)
            const reader = new FileReader();
            reader.onloadend = () => {
                setCapturedPhotoDataUrl(reader.result); // 미리보기용 Data URL 저장
                setShowSubmitModal(true); // 제출 모달 열기
                showMessage("사진이 촬영되었습니다. 코멘트를 입력하고 제출해주세요.", 'info');
            };
            reader.readAsDataURL(photoBlob);

        } catch (error) {
            console.error("사진 촬영 오류:", error);
            showMessage("사진 촬영에 실패했습니다.", 'error');
        }
    };

    const handleUploadAndSubmit = async () => {
        if (!capturedPhotoBlob) { // 저장된 Blob이 없으면 리턴
            showMessage("촬영된 사진이 없습니다.", 'error');
            return;
        }
        setUploading(true);

        const formData = new FormData();
        // 캡처된 Blob을 직접 FormData에 추가
        formData.append("file", new File([capturedPhotoBlob], "photo.jpg", { type: capturedPhotoBlob.type }));

        try {
            // 1) S3 업로드 API 호출
            const uploadResponse = await fetch("http://localhost:8080/api/files/upload", {
                method: "POST",
                body: formData,
            });

            if (!uploadResponse.ok) {
                const errorText = await uploadResponse.text();
                throw new Error(`업로드 실패: ${uploadResponse.status} ${errorText}`);
            }

            const s3Url = await uploadResponse.text();

            // 2) 인증 제출 API 호출 (comment 포함)
            const submitResponse = await fetch(`http://localhost:8080/api/verifications/${verificationId}/submit`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    fileUrl: s3Url, // S3 URL 전송
                    comment: comment,
                }),
            });

            if (!submitResponse.ok) {
                const errorText = await submitResponse.text();
                throw new Error(`제출 실패: ${submitResponse.status} ${errorText}`);
            }

            showMessage("사진 제출 성공!", 'success');
            setShowSubmitModal(false); // 모달 닫기
            navigate("/seller/verification-start"); // 제출 성공 후 인증 목록 페이지로 돌아가기
        } catch (error) {
            console.error("업로드/제출 오류:", error);
            showMessage(`오류: ${error.message}`, 'error');
        } finally {
            setUploading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 sm:p-6 font-inter">
            <div className="bg-white rounded-xl shadow-lg p-6 w-full max-w-md">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">
                    인증 사진 제출 <span className="text-blue-600">(ID: {verificationId})</span>
                </h2>

                {/* 메시지 모달 */}
                {message.text && (
                    <div className={`p-3 mb-4 rounded-lg flex items-center space-x-2 
                                    ${message.type === 'success' ? 'bg-green-100 text-green-700' :
                                      message.type === 'error' ? 'bg-red-100 text-red-700' :
                                      'bg-blue-100 text-blue-700'}`}>
                        {message.type === 'success' && <CheckCircle size={20} />}
                        {message.type === 'error' && <AlertCircle size={20} />}
                        {message.type === 'info' && <MessageSquare size={20} />}
                        <span>{message.text}</span>
                    </div>
                )}

                {/* 카메라 시작 버튼 */}
                <button 
                    onClick={startCamera} 
                    className="w-full flex items-center justify-center bg-gray-700 text-white py-3 px-4 rounded-lg text-lg font-semibold shadow-md hover:bg-gray-800 transition duration-300 ease-in-out mb-4"
                    disabled={cameraActive}
                >
                    <Camera size={20} className="mr-2" />
                    {cameraActive ? "카메라 활성화됨" : "카메라 시작"}
                </button>

                {/* 비디오 미리보기 */}
                <div className="relative w-full aspect-video bg-gray-200 rounded-lg overflow-hidden border border-gray-300">
                    <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        className="w-full h-full object-cover"
                        style={{ display: cameraActive ? 'block' : 'none' }} // 카메라 활성화 시에만 비디오 표시
                    />
                    {!cameraActive && (
                        <div className="absolute inset-0 flex items-center justify-center text-gray-500 text-center">
                            카메라를 시작해주세요.
                        </div>
                    )}
                </div>

                {/* 사진 촬영 버튼 */}
                <button
                    onClick={takePhoto}
                    className="w-full flex items-center justify-center bg-blue-600 text-white py-3 px-4 rounded-lg text-lg font-semibold shadow-md hover:bg-blue-700 transition duration-300 ease-in-out mt-4"
                    disabled={!cameraActive}
                >
                    <Camera size={20} className="mr-2" />
                    사진 촬영
                </button>

                {/* 캔버스 요소는 더 이상 필요 없으므로 제거됩니다. */}
                {/* <canvas ref={canvasRef} style={{ display: "none" }} /> */}

                {/* 제출 모달 */}
                {showSubmitModal && (
                    <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center p-4 z-50">
                        <div className="bg-white rounded-xl shadow-2xl p-6 w-full max-w-sm relative">
                            <button 
                                onClick={() => {
                                    setShowSubmitModal(false);
                                    setCapturedPhotoDataUrl(""); // 모달 닫을 때 미리보기 초기화
                                    setCapturedPhotoBlob(null); // 모달 닫을 때 Blob 초기화
                                }} 
                                className="absolute top-3 right-3 text-gray-500 hover:text-gray-700"
                            >
                                <XCircle size={24} />
                            </button>
                            <h3 className="text-xl font-bold text-center text-gray-800 mb-4">사진 확인 및 제출</h3>

                            {capturedPhotoDataUrl && (
                                <div className="mb-4 text-center">
                                    <img 
                                        src={capturedPhotoDataUrl} 
                                        alt="Captured Preview" 
                                        className="max-w-full h-auto rounded-lg shadow-sm mx-auto border border-gray-200" 
                                    />
                                </div>
                            )}

                            <textarea
                                placeholder="인증에 대한 코멘트를 입력하세요..."
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                                rows={4}
                                className="w-full border border-gray-300 rounded-lg p-3 mb-4 focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-y"
                            />

                            <button
                                onClick={handleUploadAndSubmit}
                                disabled={uploading || !capturedPhotoBlob} // Blob이 있어야 업로드 가능
                                className={`w-full flex items-center justify-center py-3 px-4 rounded-lg text-lg font-semibold shadow-md transition duration-300 ease-in-out
                                            ${uploading ? "bg-gray-400 cursor-not-allowed" : "bg-green-600 hover:bg-green-700"}
                                            text-white`}
                            >
                                {uploading ? (
                                    <>
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        업로드 중...
                                    </>
                                ) : (
                                    <>
                                        <Upload size={20} className="mr-2" />
                                        사진 업로드 및 제출
                                    </>
                                )}
                            </button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default VerificationCamera;
